mapper.select:
select min(c.qymc)qymc,min(p.xmmc) xmmc,min(d.name)yt,avg(t.dj)jj,sum(h.SCJZMJ)xsmj,count(t.houseid)xsl,sum(t.zj)zj from project p left join company c on p.company_id = c.id left join zrz z on z.xmid = p.id left join h on h.ZRZH = z.ZRZH left join dictionary d on d.code = p.xmyt and d.type='fwyt' left join (select hnt.houseid, hnt.zj,hnt.dj from house_new_trade hnt where hnt.currentstatus = 5 and hnt.houseid is not null
union all select hst.houseid, hst.zj,hst.dj from house_stock_trade hst where hst.currentstatus = 5 and hst.houseid is not null
)t on t.houseid = h.id
(instr(c.qymc,#{mc})>0 or instr(p.xmmc,#{mc})>0)
and p.xmyt = #{yt}
group by p.id
${item}
